        <Form Operation="Recharger votre compte" Send={SendA} error={Afficher}></Form>
        <Form Operation="Virement" Send={SendB}></Form>
        <Form Operation="Recharger Telephone" Send={SendC}></Form>
      </div>
    </div>
  );
}

export default App;
